import React, { useState } from 'react';
import './Calculator.css';

const Calculator = () => {
  const [displayValue, setDisplayValue] = useState('0');
  const [buffer, setBuffer] = useState('');
  const [operator, setOperator] = useState(null);
  const [previousValue, setPreviousValue] = useState(null);

  const handleNumberClick = (number) => {
    setDisplayValue((prevDisplayValue) =>
      prevDisplayValue === '0' ? number : prevDisplayValue + number
    );
  };

  const handleOperatorClick = (operator) => {
    if (buffer) {
      evaluate();
    }
    setBuffer(displayValue);
    setOperator(operator);
    setDisplayValue('0');
  };

  const evaluate = () => {
    const current = parseFloat(displayValue);
    const previous = parseFloat(buffer);

    let result;
    switch (operator) {
      case '+':
        result = previous + current;
        break;
      case '-':
        result = previous - current;
        break;
      case '*':
        result = previous * current;
        break;
      case '/':
        result = previous / current;
        break;
      default:
        break;
    }

    setDisplayValue(result.toString());
    setPreviousValue(result.toString());
    setBuffer('');
    setOperator(null);
  };

  const clear = () => {
    setDisplayValue('0');
    setBuffer('');
    setOperator(null);
    setPreviousValue(null);
  };

  return (
    <div className="calculator">
        {previousValue && (<div className="previous-value">Previous: {previousValue}</div>)}
        <div className="display">{displayValue}</div>
      <div className="buttons">
        <div className="row">
          <button onClick={() => handleNumberClick('1')}>1</button>
          <button onClick={() => handleNumberClick('2')}>2</button>
          <button onClick={() => handleNumberClick('3')}>3</button>
          <button onClick={() => handleOperatorClick('+')}>+</button>
        </div>
        <div className="row">
          <button onClick={() => handleNumberClick('4')}>4</button>
          <button onClick={() => handleNumberClick('5')}>5</button>
          <button onClick={() => handleNumberClick('6')}>6</button>
          <button onClick={() => handleOperatorClick('-')}>-</button>
        </div>
        <div className="row">
          <button onClick={() => handleNumberClick('7')}>9</button>
          <button onClick={() => handleNumberClick('8')}>8</button>
          <button onClick={() => handleNumberClick('9')}>9</button>
          <button onClick={() => handleOperatorClick('*')}>*</button>
        </div>
        <div className="row">
          <button onClick={() => clear()}>C</button>
          <button onClick={() => handleNumberClick('0')}>0</button>
          <button onClick={() => evaluate()}>=</button>
          <button onClick={() => handleOperatorClick('/')}>/</button>
        </div>
      </div>
    </div>
  );
};

export default Calculator;
